package StringDemo;

public class SringcharDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stubt
		String s = "welcom";
		System.out.println(s.charAt(3));
		System.out.println(s.contains("lc"));
		System.out.println(s.contains("m"));
		System.out.println(s.contains("om"));
		
		
		// comparing of two string
		
	System.out.println("Comparing Two String ");
		
		String m = "hello";
		String n = "Hello";
      System.out.println(m.equals(n));
      System.out.println(m.equalsIgnoreCase(n));

	}

}
