package operaters;

public class relationaloperator1 {
	//relational return true or false like boolean

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		
		System.out.println(a==b);
		System.out.println(a>b);
		
		System.out.println(a<b);
		System.out.println(a!=b);
		System.out.println(a<=b);
		System.out.println(a>=b);

	}

}
